pages.base.jade:
html
	head

		+h.headers
		block title
			title= post.title or site.title
		+h.load('/fb_static/lib/pure.css')
		+h.load('/fb_static/lib/markdown/basic.css')
		+h.load("/template/static/style.scss")
		+h.load("/template/static/snowy.html")

	body
		block header
			#nav_header
				+site.get_nav(align="right", toggle_menu=True)

		block content
		+h.back_to_top()
		+h.load("/fb_static/lib/jquery.js")
		+h.load('font')

		///全局背景音乐
		///audio(autoplay='yes')
  			///source(src='https://inz.oss-cn-beijing.aliyuncs.com/Audios/320kbit/%E6%9C%A8%E6%9D%91%E5%BC%93%20-%20%E3%81%84%E3%81%A4%E3%82%82%E4%BD%95%E5%BA%A6%E3%81%A6%E3%82%99%E3%82%82.mp3')


///hr.space
script(async='', src='//busuanzi.ibruce.info/busuanzi/2.3/busuanzi.pure.mini.js')
.footer
	span.visitors
		span#busuanzi_container_site_pv
		  | 总访问量
		  span#busuanzi_value_site_pv
			i.fa.fa-spinner
		  | 次
		span#busuanzi_container_site_uv
		  | 访客数
		  span#busuanzi_value_site_uv
			i.fa.fa-spinner
		  | 人次
		span#sitetime

	///显示建站时间的地方放置此代码 可以加上其他代HTML代码加粗颜色等
	span.elapsedtime
	script(language='javascript').	
		function siteTime(){
		var seconds = 1000;
		var minutes = seconds * 60;
		var hours = minutes * 60;
		var days = hours * 24;
		var years = days * 365;
		var today = new Date();
		var todayYear = today.getFullYear();
		var todayMonth = today.getMonth()+1;
		var todayDate = today.getDate();
		var todayHour = today.getHours();
		var todayMinute = today.getMinutes();
		var todaySecond = today.getSeconds();
		///此处填写建站时间 依次为 年,月,日,时,分,秒注意格式 半角,
		var t1 = Date.UTC(2016,06,06,12,00,00);  
		var t2 = Date.UTC(todayYear,todayMonth,todayDate,todayHour,todayMinute,todaySecond);
		var diff = t2-t1;
		var diffYears = Math.floor(diff/years);
		var diffDays = Math.floor((diff/days)-diffYears*365);
		var diffHours = Math.floor((diff-(diffYears*365+diffDays)*days)/hours);
		var diffMinutes = Math.floor((diff-(diffYears*365+diffDays)*days-diffHours*hours)/minutes);
		var diffSeconds = Math.floor((diff-(diffYears*365+diffDays)*days-diffHours*hours-diffMinutes*minutes)/seconds);
		///add a zero in front of numbers which<10
		function checkTime(i)
		{
		if (i<10)
		{
		i="0" + i
		}
		return i
		}
		diffYears=checkTime(diffYears);
		diffDays=checkTime(diffDays);
		diffHours=checkTime(diffHours);
		diffMinutes=checkTime(diffMinutes);
		diffSeconds=checkTime(diffSeconds);
		///行宽度不随着数字宽度变化而变化
		///var dateBox = document.createElement('div')
		///dateBox.style.display='inline-block'
		///datebox.style.innerHTML=“测试”
		document.getElementById("sitetime").innerHTML='\xa0'+'|'+'\xa0'+ " 已开通 "+diffYears+" 年 "+diffDays+" 天 "+diffHours+" 小时 "+diffMinutes+" 分钟<div style='display:inline-block;width:28px;'>"+diffSeconds+"</div>秒\xa0"; 
		setTimeout("siteTime()", 1000);
		};
		siteTime();

	.refresh
		///i.fa.fa-cog.fa-spin
		i.fa.fa-refresh.fa-spin

	p.copyright
	  | © 
	  script.
		  document.write("2016 ￚ " + new Date().getFullYear() + '\xa0' + '·');
	  a(href='https://balmy.life', target='_self') 雨浥轻尘
	  |  · All Rights Reserved

pages.index.html:
{% from "mixins" import make_timeline %}


{% extends "base.jade" %}

{% block content %}

<div class="content">
  <div class="page-header">
    <div class="box-blog-info"><a href="/" class="avatar"><img src="{{site.site_avatar}}" />
</a>

      <div class="info">
        <h3 class="name">{{ site.title }}</h3>

        <div class="slogan">{{ site.sub_title }}</div>

      </div>

    </div>

  </div>

  <!--Count-->
<span>{{ '%s篇文章，' %(d.get_data(types='post', return_count=true, with_page=false)) }}</span><span>{{ '{0:,}'.format(site.text_words) + '字' }}</span>

{% set entries = d.get_data(type='post',limit=300, sort='desc').group('-date:year') %}
{{ make_timeline(entries) }}{{ h.paginator() }}

</div>

{% endblock %}

pages.category+categories.html:
{% from "mixins" import make_timeline %}


{% extends "base.jade" %}

{% block content %}

<div class="content">
  <div class="page-header">
    <div class="box-blog-info"><a href="/" class="avatar"><img src="{{site.site_avatar}}" />
</a>

      <div class="info">
        <h3 class="name">{{ site.title }}</h3>

        <div class="slogan">{{ site.sub_title }}</div>

      </div>

    </div>

  </div>
{% if  posts.category %}
{% set all_posts = posts.list_obj %}

{% else %}
{% set all_posts = get_data(type='post',limit=300, sort='desc') %}

{% endif %}

{% set entries = all_posts.group('path:parent_path') %}
{{ make_timeline(entries) }}{{ h.paginator() }}

</div>

{% endblock %}

pages.mixins.html:
{% macro make_timeline(cated_posts) %}

<ul class="post_list">{% for category,posts in cated_posts %}
  <li class="item_title">
{% set dot_class = 'dot dot_%s'%loop.index %}
<span style="background: {{h.get_a_color()}}" class="{{dot_class}}" ></span>

{% set category_title = category or 'UnCategory' %}
<span id="{{h.get_dom_id(category_title)}}"  class="category_title">{{ category_title }}</span>

  </li>
{% for post in posts %}
  <li class="item_post item"><span style="background: {{h.get_a_color()}}" class="dot"></span>
<span class="post_date">{{ post.date.format('%h, %d') }}</span>
<a href="{{post.url}}"  class="post_title">{{ post.title }}</a>

  </li>

{% endfor %}
{% endfor %}
</ul>

{% endmacro %}

pages.static/style.scss:
﻿$site-color: #35b995;
$link-color: $site-color;
$link-hover-color: $site-color;

@font-face {
	font-family: 'calligraffittiregular';
	src: url('/template/static/fonts/calligraffitti-regular-webfont.eot?app=blog/fexo');
	src: url('/template/static/fonts/calligraffitti-regular-webfont.eot?app=blog/fexo#iefix') format('embedded-opentype'),
	///url('/template/static/fonts/calligraffitti-regular-webfont.woff2?app=blog/fexo') format('woff2'),
	///url('/template/static/fonts/calligraffitti-regular-webfont.woff?app=blog/fexo') format('woff'),
	///url('/template/static/fonts/calligraffitti-regular-webfont.ttf?app=blog/fexo') format('truetype'),
	///url('/template/static/fonts/calligraffitti-regular-webfont.svg#calligraffittiregular') format('svg');
	url('https://inz.oss-cn-beijing.aliyuncs.com/balmy.life/template/static/fonts/calligraffitti-regular-webfont.eot?app=blog/fexo') format('eot'),
	url('https://inz.oss-cn-beijing.aliyuncs.com/balmy.life/template/static/fonts/calligraffitti-regular-webfont.svg?app=blog/fexo') format('svg'),
	url('https://inz.oss-cn-beijing.aliyuncs.com/balmy.life/template/static/fonts/calligraffitti-regular-webfont.ttf?app=blog/fexo') format('truetype'),
	url('https://inz.oss-cn-beijing.aliyuncs.com/balmy.life/template/static/fonts/calligraffitti-regular-webfont.woff?app=blog/fexo') format('woff'),
	url('https://inz.oss-cn-beijing.aliyuncs.com/balmy.life/template/static/fonts/calligraffitti-regular-webfont.woff2#calligraffittiregular') format('woff2');
	font-weight: normal;
	font-style: normal;
}

a {
	text-decoration: none;
	color: #555;
}

html, body {
	font-family: PingFangSC-Regular, 'Roboto', Verdana, "Open Sans", "Helvetica Neue", "Helvetica", "Hiragino Sans GB", "Microsoft YaHei", "Source Han Sans CN", "WenQuanYi Micro Hei", Arial, sans-serif;
	-webkit-font-smoothing: antialiased;
	-webkit-tap-highlight-color: rgba(0, 0, 0, 0);
	overflow-x: hidden;
	overflow-y: auto;
	width: 100%;
	min-height: 100%;
	scroll-behavior: smooth;
	font-size: 16px;
  	///background:linear-gradient( 135deg, #FEB692 10%, #EA5455 100%);
  	///site bg
    ///background-image:url(/_direct/bg.jpg);
}

///footer
.footer{
	display:block!important;
  	position:absolute;
	margin:auto;
	width:100%;
	text-align:center;
   	box-sizing:border-box;
  	border:0.1px solid #EAEAEA;
  	padding:20px;
	.visitors{
	  	font-size:15px;
  		color:#999999;
	}
	.elapsedtime{
		font-size:15px;
  		color:#999999;
	}
	.copyright{
		font-size:15px;
  		color:#999999;
	}
 	.refresh{
	  	display:inline-block;
	  	font-size:15px;
  		color:#999999;
  	}
}

#nav_header{
	border-bottom: 1px solid #eee;
	.site_nav_wrap{
		.site_nav{
			a{
				font-size: 15px;
				color: #777;
				&:hover{
					color: #222;
				}
			}
		}
	}
}

$category-item-height: 30px;
$category-title-height: 40px;

.category_box {
	position: relative;
	margin-bottom: -$category-title-height/2;
	margin-left: -$category-title-height/2;
	.category_title {
		font-size: 13px;
		line-height: $category-title-height;
		position: absolute;
		top: 50%;
		width: $category-title-height;
		height: $category-title-height;
		margin-top: -$category-title-height/2;
		text-align: center;
		color: #555;
		border-radius: 50%;
		background: #f0f0f0;
	}
	.category_list {
		margin-left: 50px;
		.category_item {
			font-size: 12px;
			line-height: $category-item-height;
			display: inline-block;
			height: $category-item-height;
			margin: 5px 3px;
			padding: 0 12px;
			color: #999;
			border-radius: $category-item-height/2;
			background: #f6f6f6;
			&:hover {
				color: #333;
				background: #f0f0f0;
			}
			.category_size {
				font-family: 'calligraffittiregular';
				font-weight: bold;
			}
		}
	}
}

@media screen and (max-width: 767px) {
	.category-box {
		margin-left: 0;
	}
}



.toc_container {
	position: fixed;
	font-size: 13px;

	a {
		text-decoration: none;
		line-height: 1.1;
		color: #555555;
		&:visited {
			color: #999;
		}
		&:hover{
			color: #333;
		}
	}


	a.selected {
		color: #000000;
		font-weight: bold;
	}

	ul {
		line-height: 1.8em;
		padding-left: 0;
		list-style: none;
		li {
			list-style: none;
		}
	}

	li {
		margin: 0.5em auto;

		ul {
			padding: 0 0 0 1em;
		}
	}

	.toc_title{
		font-size: 16px;
		color: #000;
		font-weight: bold;
	}

}



.post {
	color: #555;
	margin-bottom: 3em;

	.header {
		margin: 4em 0 3.5em 0;
		h1 {
			color: #000;
			font-weight: normal;
			font-size: 2.2em;
			padding-bottom: 0.5em;
			line-height: 1.1em;
			text-align: center;
			margin: 0;
		}
		.info {
			text-align: center;
			margin: 1em 0;
			font-size: 80%;
			color: #777;
		  
			a{
				color: #333;
				margin-left: 8px;
				&:hover {
					color: #111;
					text-decoration: underline;
				}
			}
		}
	}

	.post_with_toc {
		max-width: 860px;
		margin: 0 auto;
		overflow: hidden;

		.post_container {
			padding: 0 3em 0 3.5em;
		}

		.toc_container {
			padding-left: 7em;
		}
	}

	.post_without_toc {
		max-width: 760px;
		margin: 0 auto;
	}

	@media screen and (max-width: 767px) {
		.post_with_toc, .post_without_toc {
			.post_container {
				padding: 0;
				width: 90%;
				margin: 0 auto;
			}

		}

	}
}

.hide {
	display: none;
}

.show {
	display: block;
}

.content {
	width: 500px;
	margin: 40px auto 80px;
	border-left: 4px solid #f9f9f9;
  
  	//count style
  	span {
	  color: #999;
	  font-size: 14px;
	  font-weight: normal;
	  margin: 0;
  }
  
}

.page-header {
	position: relative;
	margin-bottom: 30px;
  ///background color for header
	///background: #fff;

  .box-blog-info {
		display: block;
		.avatar, .info {
			display: inline-block;
			vertical-align: middle;
		}
		img {
			width: 60px;
			height: 60px;
			vertical-align: middle;
			border-radius: 50%;
			object-fit: cover;
			overflow: hidden;
		}
		.info {
			margin-left: 10px;
			.name {
				font-size: 24px;
				font-weight: normal;
				margin: 0;
				color: #000;
			}
			.slogan {
				display: inline-block;
				color: #999;
			}
		}
	}
}

@media screen and (min-width: 768px) {
	.page-header {
		margin-bottom: 30px;
		.home-entry {
			display: inline-block;
		}
		.box-blog-info {
			display: block;
			margin-left: -30px;
		}
	}
}

@media screen and (max-width: 767px) {
	.page-header {
		margin-bottom: 30px;
		text-align: center;
		.breadcrumb {
			display: none;
		}
		.home-entry {
			display: none;
		}
		.box-blog-info {
			.avatar {
				display: block;
				img {
					width: 60px;
					height: 60px;
					vertical-align: middle;
					border-radius: 50%;
				}
			}
			.info {
				display: inline-block;
				.name {
					display: inline-block;
					margin-top: 10px;
					margin-bottom: 8px;
				}
				.slogan {
					display: block;
				}
			}
		}
	}

}



.post_list {
	line-height: 2.8em;
	list-style: none;
	padding: 0;

	li {
		list-style: none;
	}

	.category_title {
		font-family: 'calligraffittiregular';
		font-size: 18px;
		font-weight: bold;
		color: #222;
	}

	.item_title {
		position: relative;
		margin-top: 40px;
		padding-left: 20px;
		.dot {
			position: absolute;
			top: 50%;
			left: -2px;
			width: 10px;
			height: 10px;
			margin-top: -9px;
			margin-left: -5px;
			content: ' ';
			border-radius: 50%;
		}
		.dot_1 {
			background: #3498db !important;
		}
		.dot_2 {
			background: #9b59b6 !important;
		}
		.dot_3 {
			background: #e67e22 !important;
		}
		.dot_4 {
			background: #e74c3c !important;
		}
		.dot_5 {
			background: #1abc9c !important;
		}
	}

	.item_post {
		position: relative;
		padding-left: 20px;
		&:before {
			position: absolute;
			top: 50%;
			left: -2px;
			width: 8px;
			height: 8px;
			margin-top: -4px;
			margin-left: -4px;
			content: ' ';
			border-radius: 50%;
			background: #ddd;
		}
		.post_date {
			font-size: 12px;
			display: inline-block;
			color: #888;
		}
		.post_title {
			font-size: 16px;
			font-weight: normal;
			position: relative;
			display: inline-block;
			transition-duration: .5s;
			color: #333;
			vertical-align: middle;
			text-overflow: ellipsis;
			max-width: 430px;
			white-space: nowrap;
			overflow: hidden;

			transition-propety: background-color;
			&:hover {
				color: $link-hover-color;
			}
		}
	}

}

.markdown {
	color: #555;

	h1, h2, h3, h4, h5, h6 {
		font-weight: normal;
		margin: 2.5em 0 1.3em 0;
		color: #000;
	}
	h1 {
		font-size: 24px;
	}
	h2 {
		font-size: 20px;
	}
	h3 {
		font-size: 16px;
	}
	h4 {
		font-size: 14px;
	}
	h5, h6 {
		font-size: 12px;
	}

	pre {
		display: block;
		overflow-x: auto;
		margin: 0 0 15px;
		padding: 16px;
		color: #555;
		font-size: 14px;
		border-radius: 6px;
		background-color: #f7f7f7;
		overflow-y: hidden;
	}

	a{
		color: #1abc9c;
		font-weight: bold;
	}

	blockquote{
		color: #777;
		border-left-color: #35B995;
	}
}

@media screen and (max-width: 520px) {
	.content{
		width: 90%;
	}

}


.pager {
	border-top: 0px solid #ddd;
	margin: 85px auto 35px auto;
	font-size: 14px;
	a {
		display: inline-block;
		height: 25px;
		line-height: 25px;
		text-align: center;

		width: 50%;
		&.next {
			float: right;
		}
		&:hover {
			background-color:  #cc181e;
			color: #FFF;
		}
	}
}

