	margin-left: -$category-title-height/2;
	.category_title {
		font-size: 13px;
		line-height: $category-title-height;
		position: absolute;
		top: 50%;
		width: $category-title-height;
		height: $category-title-height;
		margin-top: -$category-title-height/2;
		text-align: center;
		color: #555;
		border-radius: 50%;
		background: #f0f0f0;
	}
	.category_list {
		margin-left: 50px;
		.category_item {
			font-size: 12px;
			line-height: $category-item-height;
			display: inline-block;
			height: $category-item-height;
			margin: 5px 3px;
			padding: 0 12px;
			color: #999;
			border-radius: $category-item-height/2;
			background: #f6f6f6;
			&:hover {
				color: #333;
				background: #f0f0f0;
			}
			.category_size {
				font-family: 'calligraffittiregular';
				font-weight: bold;
			}
		}
	}
}

@media screen and (max-width: 767px) {
	.category-box {
		margin-left: 0;
	}
}



.toc_container {
	position: fixed;
	font-size: 13px;

	a {
		text-decoration: none;
		line-height: 1.1;
		color: #555555;
		&:visited {
			color: #999;
		}
		&:hover{
			color: #333;
		}
	}


	a.selected {
		color: #000000;
		font-weight: bold;
	}

	ul {
		line-height: 1.8em;
		padding-left: 0;
		list-style: none;
		li {
			list-style: none;
		}
	}

	li {
		margin: 0.5em auto;

		ul {
			padding: 0 0 0 1em;
		}
	}

	.toc_title{
		font-size: 16px;
		color: #000;
		font-weight: bold;
	}

}



.post {
	color: #555;
	margin-bottom: 3em;

	.header {
		margin: 4em 0 3.5em 0;
		h1 {
			color: #000;
			font-weight: normal;
			font-size: 2.2em;
			padding-bottom: 0.5em;
			line-height: 1.1em;
			text-align: center;
			margin: 0;
		}
		.info {
			text-align: center;
			margin: 1em 0;
			font-size: 80%;
			color: #777;
		  
			a{
				color: #333;
				margin-left: 8px;
				&:hover {
					color: #111;
					text-decoration: underline;
				}
			}
		}
	}

	.post_with_toc {
		max-width: 860px;
		margin: 0 auto;
		overflow: hidden;

		.post_container {
			padding: 0 3em 0 3.5em;
		}

		.toc_container {
			padding-left: 7em;
		}
	}

	.post_without_toc {
		max-width: 760px;
		margin: 0 auto;
	}

	@media screen and (max-width: 767px) {
		.post_with_toc, .post_without_toc {
			.post_container {
				padding: 0;
				width: 90%;
				margin: 0 auto;
			}

		}

	}
}

.hide {
	display: none;
}

.show {
	display: block;
}

.content {
	width: 500px;
	margin: 40px auto 80px;
	border-left: 4px solid #f9f9f9;
  
  	//count style
  	span {
	  color: #999;
	  font-size: 14px;
	  font-weight: normal;
	  margin: 0;
  }
  
}

.page-header {
	position: relative;
	margin-bottom: 30px;
  ///background color for header
	///background: #fff;

  .box-blog-info {
		display: block;
		.avatar, .info {
			display: inline-block;
			vertical-align: middle;
		}
		img {
			width: 60px;
			height: 60px;
			vertical-align: middle;
			border-radius: 50%;
			object-fit: cover;
			overflow: hidden;
		}
		.info {
			margin-left: 10px;
			.name {
				font-size: 24px;
				font-weight: normal;
				margin: 0;
				color: #000;
			}
			.slogan {
				display: inline-block;
				color: #999;
			}
		}
	}
}

@media screen and (min-width: 768px) {
	.page-header {
		margin-bottom: 30px;
		.home-entry {
			display: inline-block;
		}
		.box-blog-info {
			display: block;
			margin-left: -30px;
		}
	}
}

@media screen and (max-width: 767px) {
	.page-header {
		margin-bottom: 30px;
		text-align: center;
		.breadcrumb {
			display: none;
		}
		.home-entry {
			display: none;
		}
		.box-blog-info {
			.avatar {
				display: block;
				img {
					width: 60px;
					height: 60px;
					vertical-align: middle;
					border-radius: 50%;
				}
			}
			.info {
				display: inline-block;
				.name {
					display: inline-block;
					margin-top: 10px;
					margin-bottom: 8px;
				}
				.slogan {
					display: block;
				}
			}
		}
	}

}



.post_list {
	line-height: 2.8em;
	list-style: none;
	padding: 0;

	li {
		list-style: none;
	}

	.category_title {
		font-family: 'calligraffittiregular';
		font-size: 18px;
		font-weight: bold;
		color: #222;
	}

	.item_title {
		position: relative;
		margin-top: 40px;
		padding-left: 20px;
		.dot {
			position: absolute;
			top: 50%;
			left: -2px;
			width: 10px;
			height: 10px;
			margin-top: -9px;
			margin-left: -5px;
			content: ' ';
			border-radius: 50%;
		}
		.dot_1 {
			background: #3498db !important;
		}
		.dot_2 {
			background: #9b59b6 !important;
		}
		.dot_3 {
			background: #e67e22 !important;
		}
		.dot_4 {
			background: #e74c3c !important;
		}
		.dot_5 {
			background: #1abc9c !important;
		}
	}

	.item_post {
		position: relative;
		padding-left: 20px;
		&:before {
			position: absolute;
			top: 50%;
			left: -2px;
			width: 8px;
			height: 8px;
			margin-top: -4px;
			margin-left: -4px;
			content: ' ';
			border-radius: 50%;
			background: #ddd;
		}
		.post_date {
			font-size: 12px;
			display: inline-block;
			color: #888;
		}
		.post_title {
			font-size: 16px;
			font-weight: normal;
			position: relative;
			display: inline-block;
			transition-duration: .5s;
			color: #333;
			vertical-align: middle;
			text-overflow: ellipsis;
			max-width: 430px;
			white-space: nowrap;
			overflow: hidden;

			transition-propety: background-color;
			&:hover {
				color: $link-hover-color;
			}
		}
	}

}

.markdown {
	color: #555;

	h1, h2, h3, h4, h5, h6 {
		font-weight: normal;
		margin: 2.5em 0 1.3em 0;
		color: #000;
	}
	h1 {
		font-size: 24px;
	}
	h2 {
		font-size: 20px;
	}
	h3 {
		font-size: 16px;
	}
	h4 {
		font-size: 14px;
	}
	h5, h6 {
		font-size: 12px;
	}

	pre {
		display: block;
		overflow-x: auto;
		margin: 0 0 15px;
		padding: 16px;
		color: #555;
		font-size: 14px;
		border-radius: 6px;
		background-color: #f7f7f7;
		overflow-y: hidden;
	}

	a{
		color: #1abc9c;
		font-weight: bold;
	}

	blockquote{
		color: #777;
		border-left-color: #35B995;
	}
}

@media screen and (max-width: 520px) {
	.content{
		width: 90%;
	}

}


.pager {
	border-top: 0px solid #ddd;
	margin: 85px auto 35px auto;
	font-size: 14px;
	a {
		display: inline-block;
		height: 25px;
		line-height: 25px;
		text-align: center;

		width: 50%;
		&.next {
			float: right;
		}
		&:hover {
			background-color:  #cc181e;
			color: #FFF;
		}
	}
}
